/*!
 * Bootstrap bundle JS v5.3.0
 * Direct include version
 */
document.addEventListener("DOMContentLoaded", function() {
  // Verificar si Bootstrap ya está definido
  if (typeof bootstrap === 'undefined') {
    console.log('Cargando Bootstrap JS...');
    
    // Incluir Bootstrap directamente en el documento
    const bootstrapScript = document.createElement('script');
    bootstrapScript.src = 'https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js';
    bootstrapScript.onload = function() {
      console.log('Bootstrap cargado correctamente');
      // Inicializar los componentes de bootstrap si es necesario
      if (document.getElementById('modalCerrar')) {
        window.modalCerrar = new bootstrap.Modal(document.getElementById('modalCerrar'));
        console.log('Modal de cierre inicializado');
      }
    };
    document.head.appendChild(bootstrapScript);
  } else {
    console.log('Bootstrap ya está cargado');
  }
}); 